    await User.create({ 
        email: 'client@gmail.com', 
        businessName: 'ESCUADRON SECURITY S.A.C', 
        name: 'Charles Castillo', 
        jobTitle: 'Gerente General', 
        phone: '977774529', 
        username: 'cliente69', 
        password: hashedPassword,
        roleId: 1
    })
    await User.create({ 
        email: 'admin@gmail.com', 
        businessName: 'WOST S.A.C', 
        name: 'Marcelo Florez', 
        jobTitle: 'Gerente General', 
