"use strict";

const express = require("express");
const dotenv = require("dotenv");
const swaggerJsDoc = require("swagger-jsdoc");
const swaggerUi = require("swagger-ui-express");
const cors = require("cors");
const paypal = require("@paypal/checkout-server-sdk");
const {
  createDefaultData
} = require("./utils/data-default");
const path = require('path');
// Cargar variables de entorno
dotenv.config();
const app = express();

// Middleware para parsear JSON
app.use(express.json());

// Configurar CORS
const corsOptions = {
  origin: ['*'],
  // Permitir todos los orígenes
  methods: 'GET,HEAD,PUT,PATCH,POST,DELETE',
  credentials: true,
  // Permitir cookies y credenciales
  allowedHeaders: ['Content-Type', 'Authorization'] // Permitir estos encabezados
};
app.use(cors(corsOptions));

// Configuración de la base de datos
const sequelize = require('./database');

// Associations
const {
  User
} = require("./models/user.model");
const {
  Role
} = require("./models/role.model");
User.belongsTo(Role, {
  foreignKey: 'roleId',
  as: 'roles'
});
Role.hasMany(User, {
  foreignKey: 'roleId',
  as: 'usuarios'
});

// Verificar conexión a la base de datos
sequelize.authenticate().then(() => console.log("Conectado a la base de datos MYSQL")).catch(err => console.error("No se pudo conectar a la base de datos", err));
sequelize.sync({
  // force: true 
}).then(() => console.log("Modelos sincronizados con la base de datos")).catch(err => console.error("Error al sincronizar los modelos", err)).finally(() => {
  // createDefaultData()
});

// Configuración de Swagger
const swaggerOptions = {
  swaggerDefinition: {
    openapi: "3.0.0",
    info: {
      title: "API de TKMT3",
      version: "1.0.0",
      description: "API de uso sospechoso"
    },
    servers: [{
      url: "http://localhost:3000"
    }]
  },
  apis: ["./src/routes/*.js"]
};
const environment = new paypal.core.SandboxEnvironment(process.env.PAYPAL_CLIENT_ID, process.env.PAYPAL_CLIENT_SECRET);
const client = new paypal.core.PayPalHttpClient(environment);

// Endpoint para crear una orden de PayPal
app.post("/api/paypal/create-order", async (req, res) => {
  const {
    amount,
    currency
  } = req.body;
  const request = new paypal.orders.OrdersCreateRequest();
  request.prefer("return=representation");
  request.requestBody({
    intent: "CAPTURE",
    purchase_units: [{
      amount: {
        currency_code: currency || "USD",
        value: amount || "10.00" // Monto predeterminado
      }
    }]
  });
  try {
    const order = await client.execute(request);
    res.status(200).json({
      id: order.result.id
    });
  } catch (error) {
    console.error("Error al crear la orden:", error);
    res.status(500).json({
      error: error.message
    });
  }
});

// Endpoint para capturar una orden de PayPal
app.post("/api/paypal/capture-order", async (req, res) => {
  const {
    orderId
  } = req.body;
  const request = new paypal.orders.OrdersCaptureRequest(orderId);
  request.requestBody({});
  try {
    const capture = await client.execute(request);
    res.status(200).json({
      capture: capture.result
    });
  } catch (error) {
    console.error("Error al capturar la orden:", error);
    res.status(500).json({
      error: error.message
    });
